    private void setupActivityWidgets(){
        mProgressbar = (ProgressBar) findViewById(R.id.profileProgressBar);
        mProgressbar.setVisibility(View.GONE);
        profilePhoto=(ImageView) findViewById(R.id.profile_photo);
     }


    // tri tochki
    private void setupToolbar()
    {
        Toolbar toolbar = (Toolbar) findViewById(R.id.profileToolbar);
        setSupportActionBar(toolbar);

        ImageView profileMenu=(ImageView) findViewById(R.id.profileMenuTriTochki);
        profileMenu.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Log.d(TAG, "onClick: navigating to account setting");
                Intent intent= new Intent(mContext, AccountSettingActivity.class);
                startActivity(intent);

            }
        });

    }





    // bottom nav view ex dlya 5 huynyushek karoche

    private void setupNavigationView()
    {
        Log.d(TAG, "setupNavigationView: setting up BottomnavigationView");
        BottomNavigationViewEx bottomNavigationViewEx =(BottomNavigationViewEx) findViewById(R.id.bottomNavViewBar);
        BottomNavigationViewHelper.setupBottomnavigationView(bottomNavigationViewEx);


        BottomNavigationViewHelper.enableNavigation(mContext, bottomNavigationViewEx);

        Menu menu = bottomNavigationViewEx.getMenu();
        MenuItem menuItem = menu.getItem(4);
        menuItem.setChecked(true);
    }


